import React, { useState } from "react";
import CardStats from "./CardStats";
import "./Card.css";

const Card = (props) => {
  const [cardName, setCardName] = useState("");

  const onCardClickHandler = () => {
    setCardName(props.name);
    return <CardStats cardName={cardName} />;
  };

  return (
    <div className="card--container" onClick={onCardClickHandler}>
      <img className="card--logo" src={props.logo} alt="Company Logo" />
      <p className="card--description">{props.description}</p>
      <p className="card--rating">{props.rating}</p>
    </div>
  );
};

export default Card;
